mapper.update:
UPDATE qczy_many_file
SET
task_id = CASE

    WHEN id = #{entity.id} THEN #{entity.taskId}

END,
user_id = CASE

    WHEN id = #{entity.id} THEN #{entity.userId}

END,
audit_user_id = CASE

    WHEN id = #{entity.id} THEN #{entity.auditUserId}

END,
file_id = CASE

    WHEN id = #{entity.id} THEN #{entity.fileId}

END,
is_approve = CASE

    WHEN id = #{entity.id} THEN #{entity.isApprove}

END,
not_pass_message = CASE

    WHEN id = #{entity.id} THEN #{entity.notPassMessage}

END
WHERE id IN

    #{entity.id}

mapper.select:
SELECT *
FROM qczy_many_file
WHERE task_id = #{taskId}
AND file_id IN

    #{fileId}

AND user_id = #{userId}
